import React from 'react';
import { Table, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



class Alunos extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            alunos: [],
            nome: '',
            email: ''
        };
    }

    componentDidMount() {
        const alunosData = localStorage.getItem('alunos');

        if (alunosData) {
            this.setState({ alunos: JSON.parse(alunosData) });
        }
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    addAluno = () => {
        const { nome, email } = this.state;

        if (nome && email) {
            const novoAluno = {
                id: Date.now(),
                nome: nome,
                email: email
            };

            const alunosAtualizados = [...this.state.alunos, novoAluno];

            this.setState({ alunos: alunosAtualizados, nome: '', email: '' }, () => {
                localStorage.setItem('alunos', JSON.stringify(alunosAtualizados));
            });
        }
    }

    deleteAluno = (id) => {
        const novosAlunos = this.state.alunos.filter(aluno => aluno.id !== id);

        this.setState({ alunos: novosAlunos }, () => {
            localStorage.setItem('alunos', JSON.stringify(novosAlunos));
        });
    }

    render() {
        const { nome, email, alunos } = this.state;

        return (
            <div>
                <h1>Alunos</h1>
                <div>
                    <input type="text" name="nome" placeholder="Nome" value={nome} onChange={this.handleChange} />
                    <input type="email" name="email" placeholder="Email" value={email} onChange={this.handleChange} />
                    <button onClick={this.addAluno}>Adicionar</button>
                </div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {alunos.map(aluno => (
                            <tr key={aluno.id}>
                                <td>{aluno.nome}</td>
                                <td>{aluno.email}</td>
                                <td>
                                    <button className="btn btn-secundary" onClick={() => this.deleteAluno(aluno.id)}>Excluir</button>
                                    <button className="btn btn-danger" onClick={() => this.deleteAluno(aluno.id)}>Excluir</button>
                                    <Button variant="danger" onClick={() => this.deleteAluno(aluno.id)}>Excluir</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default Alunos;
