import React from 'react';
import { Table, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



class Alunos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            nome: '',
            email: '',
            alunos: []
        }
    }

    renderTabela() {
        return <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Nome</th>
                    <th>E-mail</th>
                    <th>Opções</th>
                </tr>
            </thead>
            <tbody>

                {
                    this.state.alunos.map((aluno) =>
                        <tr>
                            <td>{aluno.nome}</td>
                            <td>{aluno.email}</td>
                            <td><Button variant="secondary">Editar</Button> <Button variant="danger" onClick={() => this.deletar.Aluno()}>Excluir</Button>{' '}</td>

                        </tr>
                    )
                }

            </tbody>
        </Table>
    }
    render() {
        return (
            <div>
                {this.renderForm()}
                {this.renderTabela()}
            </div>
        )
    }
}

export default Alunos;
