import React from 'react';
import { Table, Button, Form, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class Alunos extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            alunos: [],
            nome: '',
            email: '',
            editando: false,
            alunoEditandoId: null,
            emailValido: true,
        };
    }

    componentDidMount() {
        const alunosData = localStorage.getItem('alunos');

        if (alunosData) {
            this.setState({ alunos: JSON.parse(alunosData) });
        }
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    validarEmail = (email) => {
        const regex = /\S+@\S+\.\S+/;
        return regex.test(email);
    }

    adicionarAluno = () => {
        const { nome, email, editando, alunoEditandoId } = this.state;

        if (nome && email && this.validarEmail(email)) {
            const novoAluno = {
                id: Date.now(),
                nome: nome,
                email: email,
            };

            let alunosAtualizados = [...this.state.alunos];
            if (editando) {
                const alunoEditandoIndex = alunosAtualizados.findIndex(aluno => aluno.id === alunoEditandoId);
                alunosAtualizados[alunoEditandoIndex] = novoAluno;
            } else {
                alunosAtualizados.push(novoAluno);
            }

            this.setState({ alunos: alunosAtualizados, nome: '', email: '', editando: false, alunoEditandoId: null, emailValido: true }, () => {
                localStorage.setItem('alunos', JSON.stringify(alunosAtualizados));
            });
        } else {
            this.setState({ emailValido: false });
        }
    }

    editarAluno = (id) => {
        const alunoEditando = this.state.alunos.find(aluno => aluno.id === id);
        if (alunoEditando) {
            this.setState({ nome: alunoEditando.nome, email: alunoEditando.email, editando: true, alunoEditandoId: id, emailValido: true });
        }
    }

    excluirAluno = (id) => {
        const novosAlunos = this.state.alunos.filter(aluno => aluno.id !== id);

        this.setState({ alunos: novosAlunos }, () => {
            localStorage.setItem('alunos', JSON.stringify(novosAlunos));
        });
    }

    render() {
        const { nome, email, alunos, editando, emailValido } = this.state;

        return (
            <div>
                <h1>Alunos</h1>
                <div>
                    <Form.Group controlId="nome">
                        <Form.Label>Nome</Form.Label>
                        <Form.Control type="text" name="nome" value={nome} onChange={this.handleChange} />
                    </Form.Group>
                    <Form.Group controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" name="email" value={email} onChange={this.handleChange} isInvalid={!emailValido} />
                        {!emailValido && <Alert variant="danger">Por favor, insira um email válido.</Alert>}
                    </Form.Group>
                    <Button variant="primary" onClick={this.adicionarAluno}>
                        {editando ? 'Salvar' : 'Adicionar'}
                    </Button>
                </div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {alunos.map(aluno => (
                            <tr key={aluno.id}>
                                <td>{aluno.nome}</td>
                                <td>{aluno.email}</td>
                                <td>
                                    <Button variant="primary" onClick={() => this.editarAluno(aluno.id)}>Editar</Button>{' '}
                                    <Button variant="danger" onClick={() => this.excluirAluno(aluno.id)}>Excluir</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default Alunos;
