import React, { useState } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';

function Teste() {
    const [show, setShow] = useState(false);
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [editando, setEditando] = useState(false);
    const [alunoEditandoId, setAlunoEditandoId] = useState(null);
    const [alunos, setAlunos] = useState([]);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    React.useEffect(() => {
        // Chamado após o componente ser montado no DOM
        // Verifica se há dados de alunos no localStorage e atualiza o estado
        const alunosData = localStorage.getItem('alunos');

        if (alunosData) {
            setAlunos(JSON.parse(alunosData));
        }
    }, []);

    const handleChange = (event) => {
        // Manipula o evento de mudança em campos de entrada e atualiza o estado correspondente
        if (event.target.name === 'nome') {
            setNome(event.target.value);
        } else if (event.target.name === 'email') {
            setEmail(event.target.value);
        }
    };

    const adicionarAluno = () => {
        // Função para adicionar ou editar um aluno
        if (nome && email && validarEmail(email)) {
            // Verifica se o nome e o email são válidos e se o email está no formato correto
            const novoAluno = {
                id: Date.now(), // Gera um ID único baseado no timestamp
                nome: nome,
                email: email,
            };

            let alunosAtualizados = [...alunos];
            if (editando) {
                // Se estiver no modo de edição, substitui o aluno existente com o novo aluno
                const alunoEditandoIndex = alunosAtualizados.findIndex((aluno) => aluno.id === alunoEditandoId);
                alunosAtualizados[alunoEditandoIndex] = novoAluno;
            } else {
                // Caso contrário, adiciona o novo aluno ao array de alunos
                alunosAtualizados.push(novoAluno);
            }

            // Atualiza o estado, limpando os campos e o modo de edição, e salva no localStorage
            setAlunos(alunosAtualizados);
            setNome('');
            setEmail('');
            setEditando(false);
            setAlunoEditandoId(null);

            localStorage.setItem('alunos', JSON.stringify(alunosAtualizados));
        }
    };

    const editarAluno = (id) => {
        // Função para entrar no modo de edição de um aluno
        const alunoEditando = alunos.find((aluno) => aluno.id === id);
        if (alunoEditando) {
            // Se o aluno existe, atualiza o estado com o nome, email e informações de edição
            setNome(alunoEditando.nome);
            setEmail(alunoEditando.email);
            setEditando(true);
            setAlunoEditandoId(id);
        }
    };

    const excluirAluno = (id) => {
        // Função para excluir um aluno
        const novosAlunos = alunos.filter((aluno) => aluno.id !== id);

        // Atualiza o estado e salva no localStorage
        setAlunos(novosAlunos);
        localStorage.setItem('alunos', JSON.stringify(novosAlunos));
    };

    const validarEmail = (email) => {
        // Função para validar o formato de um email usando uma expressão regular
        const re = /\S+@\S+\.\S+/;
        return re.test(email);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        adicionarAluno();
    };

    return (
        <div>
            <Button variant="primary" onClick={handleShow}>
                Adicionar Aluno
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{editando ? 'Editar Aluno' : 'Adicionar Aluno'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formNome">
                            <Form.Label>Nome</Form.Label>
                            <Form.Control type="text" name="nome" value={nome} onChange={handleChange} />
                        </Form.Group>
                        <Form.Group controlId="formEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email" name="email" value={email} onChange={handleChange} />
                        </Form.Group>
                        <Button variant="primary" type="submit">
                            {editando ? 'Editar' : 'Adicionar'}
                        </Button>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Fechar
                    </Button>
                </Modal.Footer>
            </Modal>

            <ul>
                {alunos.map((aluno) => (
                    <li key={aluno.id}>
                        {aluno.nome} - {aluno.email}
                        <Button variant="info" onClick={() => editarAluno(aluno.id)}>
                            Editar
                        </Button>
                        <Button variant="danger" onClick={() => excluirAluno(aluno.id)}>
                            Excluir
                        </Button>
                    </li>
                ))}
            </ul>
        </div>
    );

}

export default Teste
