import React from 'react';
import { Table } from 'react-bootstrap';


class Alunos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            alunos: [
                { 'id': 1, 'nome': 'Luiz Guilherme Oliveira', 'email': 'luizgui@gmail.com' },
                { 'id': 2, 'nome': 'Juan Carlos Oliveira', 'email': 'juancso69@gmail.com' },
                { 'id': 3, 'nome': 'Bruno Santos', 'email': 'bruno@gmail.com' }
            ]
        }
    }

    /*componentDidMount() {
        // Verifica se há dados salvos no localStorage
        const alunosData = localStorage.getItem('alunos');

        if (alunosData) {
            // Se houver dados, atualiza o estado com eles
            this.setState({ alunos: JSON.parse(alunosData) });
        } else {
            // Caso contrário, salva os dados iniciais no localStorage
            localStorage.setItem('alunos', JSON.stringify(this.state.alunos));
        }
    }*/

    /*componentDidMount() {
        fetch("https://localhost:5001/api/alunos")
            .then(resposta => resposta.json())
            .then(dados => {
                this.setState({alunos : dados})
            })
    }

    componentWillUnmount() {
        alert("O componente Alunos foi desmontado!")
    }*/

    render() {
        return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Opções</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        this.state.alunos.map((aluno) =>
                            <tr>
                                <td>{aluno.nome}</td>
                                <td>{aluno.email}</td>
                                <td>Atualizar Excluir</td>
                                <button onClick={() => this.deleteAluno(aluno.id)}>Excluir</button>
                            </tr>
                        )
                    }

                </tbody>
            </Table>
        )
    }
}

export default Alunos;
