import React from 'react';
import { Table, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



class Alunos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            alunos: [
                { 'id': 1, 'nome': 'Luiz Guilherme Oliveira', 'email': 'luizgui@gmail.com' },
                { 'id': 2, 'nome': 'Juan Carlos Oliveira', 'email': 'juancso69@gmail.com' },
                { 'id': 3, 'nome': 'Bruno Santos', 'email': 'bruno@gmail.com' }
            ]
        }
    }



    /*componentDidMount() {
        // Verifica se há dados salvos no localStorage
        const alunosData = localStorage.getItem('alunos');
    
        if (alunosData) {
            // Se houver dados, atualiza o estado com eles
            this.setState({ alunos: JSON.parse(alunosData) });
        } else {
            // Caso contrário, salva os dados iniciais no localStorage
            localStorage.setItem('alunos', JSON.stringify(this.state.alunos));
        }
    }*/

    /*componentDidMount() {
        fetch("https://localhost:5001/api/alunos")
            .then(resposta => resposta.json())
            .then(dados => {
                this.setState({alunos : dados})
            })
    }
    
    componentWillUnmount() {
        alert("O componente Alunos foi desmontado!")
    }*/

    renderTabela() {
        return <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Nome</th>
                    <th>E-mail</th>
                    <th>Opções</th>
                </tr>
            </thead>
            <tbody>

                {
                    this.state.alunos.map((aluno) =>
                        <tr>
                            <td>{aluno.nome}</td>
                            <td>{aluno.email}</td>
                            <td><Button variant="secondary">Editar</Button> <Button variant="danger" onClick={() => this.deletar.Aluno()}>Excluir</Button>{' '}</td>

                        </tr>
                    )
                }

            </tbody>
        </Table>
    }
    render() {
        return (
            <div>
                <Form>
                    <Form.Group className="mb-3" controlId="formBasicNome">
                        <Form.Label>Nome</Form.Label>
                        <Form.Control type="text" placeholder="Digite seu nome" />
                    </Form.Group>
                    
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Digite seu email" />
                        
                    </Form.Group>

                    
                    
                    <Button variant="primary" type="submit">
                        Adicionar
                    </Button>
                </Form>
                {this.renderTabela()}
            </div>
        )
    }
}

export default Alunos;
